  sqlite3VdbeRunOnlyOnce(v);
  pParse->nMem = 2;

  

  iDb = sqlite3TwoPartName(pParse, pId1, pId2, &pId);
  if( iDb<0 ) return;
  pDb = &db->aDb[iDb];

  


